import Input from "./Input";
import Btn from "./Btn";
import ProfileImg from "./ProfileImg";

function CommentsForm() {
  return (
    <section className="comments-form">
      <ProfileImg image="./Images/Mohan-muruge.jpg" />
      <div className="comments-form__right">
        <h4 className="comments-form__title">JOIN THE CONVERSATI</h4>
        <form action="post">
          <Input
            type="text"
            placeholder="That was easily the most spectacular BMX moment ever."
            className="input__text__comments"
          />
          <Btn type="submit" className="comments-form__btn" text="COMMENT" />
        </form>
      </div>
    </section>
  );
}

export default CommentsForm;
