import CommentsForm from "./CommentsForm";
import Comment from "./Comment";
function VideoDesc(props) {
  let desc = props.desc;
  const commentArray = props.comments;
  return (
    <section className="video-desc">
      <h1 className="video-desc__title">{desc.title}</h1>
      <h4 className="video-desc__channel">{desc.channel}</h4>
      <h4 className="video-desc__author-date">{desc.date}</h4>
      <img src="" alt="views icon" />
      <h4 className="video-desc__views-number"></h4>
      <img src="" alt="likes icon" />
      <h4 className="video-desc__likes-number"></h4>
      <p className="video-desc__text">{props.desc.description}</p>
      <h4>3 Comments</h4>
      <CommentsForm />
      <ul>
        {commentArray.sort().map((comment) => {
          return (
            <Comment
              image={comment.image}
              name={comment.name}
              date={comment.time}
              comment={comment.text}
            />
          );
        })}
      </ul>
    </section>
  );
}

export default VideoDesc;
