import CommentsForm from "./CommentsForm";
function VideoDesc(props) {
  let desc = props.desc;

  return (
    <section className="video-desc">
      <h1 className="video-desc__title">{desc.title}</h1>
      <h4 className="video-desc__channel">{desc.channel}</h4>
      <h4 className="video-desc__author-date">{desc.timestamp}</h4>
      <img src="" alt="views icon" />
      <h4 className="video-desc__views-number"></h4>
      <img src="" alt="likes icon" />
      <h4 className="video-desc__likes-number"></h4>
      <p className="video-desc__text"></p>
      <h4>3 Comments</h4>
      <CommentsForm />
    </section>
  );
}

export default VideoDesc;
